import React from 'react';
import { FaPhone, FaEnvelope, FaMapMarkerAlt, FaWhatsapp } from 'react-icons/fa';

function Contact() {
  return (
    <div className="relative min-h-screen flex flex-col justify-center items-center bg-gradient-to-br from-blue-50 via-purple-50 to-pink-50 p-4 overflow-hidden">
      {/* Animated background elements */}
      <div className="absolute inset-0 overflow-hidden">
        <div className="absolute -top-40 -right-40 w-80 h-80 bg-purple-300 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob"></div>
        <div className="absolute -bottom-40 -left-40 w-80 h-80 bg-yellow-300 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-2000"></div>
        <div className="absolute top-40 left-20 w-80 h-80 bg-pink-300 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-blob animation-delay-4000"></div>

        {/* Floating particles */}
        {[...Array(20)].map((_, i) => (
          <div
            key={i}
            className="absolute bg-blue-400/20 rounded-full animate-float"
            style={{
              width: `${Math.random() * 20 + 5}px`,
              height: `${Math.random() * 20 + 5}px`,
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${Math.random() * 5}s`,
              animationDuration: `${Math.random() * 10 + 10}s`
            }}
          />
        ))}
      </div>

      {/* Contact card */}
      <div className="w-full max-w-2xl bg-gradient-to-br from-blue-600 via-purple-600 to-blue-700 text-white rounded-2xl shadow-2xl p-6 sm:p-8 space-y-6 mx-auto transition-all duration-500 transform hover:scale-105 hover:shadow-3xl border-4 border-white animate-slideUp floating-card">
        <h2 className="text-3xl sm:text-4xl font-bold mb-6 text-center text-yellow-300 drop-shadow-lg animate-pulse">
          Contact Information
        </h2>

        {/* Contact Details List */}
        <div className="space-y-6">

          {/* Phone Number */}
          <div className="flex items-center space-x-4 p-4 bg-blue-500/80 rounded-xl hover:bg-blue-400 transition-all duration-300 cursor-pointer border-2 border-blue-400 hover:border-blue-300 shadow-lg hover:shadow-xl hover:scale-105 animate-slideLeft stagger-1">
            <div className="bg-blue-600 p-3 rounded-lg animate-bounce-slow">
              <FaPhone className="w-6 h-6 sm:w-8 sm:h-8" />
            </div>
            <div>
              <p className="text-lg sm:text-xl font-bold">Phone Number</p>
              <p className="text-sm sm:text-lg text-blue-100">+254 715657800</p>
            </div>
          </div>

          {/* Email */}
          <div className="flex items-center space-x-4 p-4 bg-blue-500/80 rounded-xl hover:bg-blue-400 transition-all duration-300 cursor-pointer border-2 border-blue-400 hover:border-blue-300 shadow-lg hover:shadow-xl hover:scale-105 animate-slideLeft stagger-2">
            <div className="bg-blue-600 p-3 rounded-lg animate-bounce-slow animation-delay-200">
              <FaEnvelope className="w-6 h-6 sm:w-8 sm:h-8" />
            </div>
            <div>
              <p className="text-lg sm:text-xl font-bold">Email Address</p>
              <p className="text-sm sm:text-lg text-blue-100">erastusngamau90@gmail.com</p>
            </div>
          </div>

          {/* Location */}
          <div className="flex items-center space-x-4 p-4 bg-blue-500/80 rounded-xl hover:bg-blue-400 transition-all duration-300 cursor-pointer border-2 border-blue-400 hover:border-blue-300 shadow-lg hover:shadow-xl hover:scale-105 animate-slideLeft stagger-3">
            <div className="bg-blue-600 p-3 rounded-lg animate-bounce-slow animation-delay-400">
              <FaMapMarkerAlt className="w-6 h-6 sm:w-8 sm:h-8" />
            </div>
            <div>
              <p className="text-lg sm:text-xl font-bold">Location</p>
              <p className="text-sm sm:text-lg text-blue-100">Nairobi, Kenya</p>
            </div>
          </div>

          {/* WhatsApp - clickable */}
          <a
            href="https://wa.me/254714287705"
            target="_blank"
            rel="noopener noreferrer"
            className="flex items-center space-x-4 p-4 bg-green-500/80 rounded-xl hover:bg-green-400 transition-all duration-300 cursor-pointer border-2 border-green-400 hover:border-green-300 shadow-lg hover:shadow-xl hover:scale-105 animate-slideLeft stagger-4 relative overflow-hidden group"
          >
            <div className="absolute inset-0 bg-white/20 transform -translate-x-full group-hover:translate-x-full transition-transform duration-1000"></div>
            <div className="bg-green-600 p-3 rounded-lg animate-pulse-slow">
              <FaWhatsapp className="w-6 h-6 sm:w-8 sm:h-8" />
            </div>
            <div>
              <p className="text-lg sm:text-xl font-bold">WhatsApp</p>
              <p className="text-sm sm:text-lg text-green-100">+254 715657800</p>
            </div>
          </a>
        </div>

        {/* Additional Contact Info */}
        <div className="mt-8 p-4 bg-blue-500/50 rounded-xl border-2 border-blue-400 animate-fadeIn">
          <p className="text-center text-yellow-300 font-semibold text-lg animate-pulse">
            ðŸ“ž We're available 24/7 for your inquiries!
          </p>
          <p className="text-center text-blue-100 mt-2 animate-fadeIn animation-delay-500">
            Call or WhatsApp us for immediate assistance
          </p>
        </div>
      </div>

      {/* WhatsApp fixed button - Same as Home and Booking pages */}
      <a
        href="https://wa.me/254714287705"
        target="_blank"
        rel="noopener noreferrer"
        className="fixed bottom-6 right-4 md:bottom-10 md:right-6 bg-green-500 text-white p-4 md:p-6 rounded-full shadow-2xl hover:bg-green-400 transition-all transform hover:scale-110 z-50 animate-bounce-slow animate-pulse-slow"
        aria-label="Chat with us on WhatsApp"
      >
        <FaWhatsapp className="text-2xl md:text-4xl" />
      </a>

      {/* Custom styles */}
      <style jsx>{`
        @keyframes float {
          0% { transform: translateY(0px) rotate(0deg); }
          33% { transform: translateY(-20px) rotate(5deg); }
          66% { transform: translateY(10px) rotate(-5deg); }
          100% { transform: translateY(0px) rotate(0deg); }
        }

        @keyframes blob {
          0% { transform: translate(0px, 0px) scale(1); }
          33% { transform: translate(30px, -50px) scale(1.1); }
          66% { transform: translate(-20px, 20px) scale(0.9); }
          100% { transform: translate(0px, 0px) scale(1); }
        }

        @keyframes slideUp {
          from {
            opacity: 0;
            transform: translateY(50px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes slideLeft {
          from {
            opacity: 0;
            transform: translateX(-50px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        @keyframes fadeIn {
          from {
            opacity: 0;
          }
          to {
            opacity: 1;
          }
        }

        @keyframes bounce-slow {
          0%, 100% { transform: translateY(0); }
          50% { transform: translateY(-10px); }
        }

        @keyframes pulse-slow {
          0%, 100% { opacity: 1; }
          50% { opacity: 0.8; }
        }

        .floating-card {
          animation: float 6s ease-in-out infinite;
        }

        .animate-blob {
          animation: blob 15s infinite;
        }

        .animate-slideUp {
          animation: slideUp 0.8s ease-out;
        }

        .animate-slideLeft {
          opacity: 0;
          animation: slideLeft 0.6s ease-out forwards;
        }

        .animate-fadeIn {
          opacity: 0;
          animation: fadeIn 1s ease-out forwards;
        }

        .animate-bounce-slow {
          animation: bounce-slow 3s infinite;
        }

        .animate-pulse-slow {
          animation: pulse-slow 3s infinite;
        }

        .stagger-1 { animation-delay: 0.1s; }
        .stagger-2 { animation-delay: 0.2s; }
        .stagger-3 { animation-delay: 0.3s; }
        .stagger-4 { animation-delay: 0.4s; }

        .animation-delay-200 {
          animation-delay: 0.2s;
        }

        .animation-delay-400 {
          animation-delay: 0.4s;
        }

        .animation-delay-500 {
          animation-delay: 0.5s;
        }

        .animation-delay-2000 {
          animation-delay: 2s;
        }

        .animation-delay-4000 {
          animation-delay: 4s;
        }
      `}</style>
    </div>
  );
}

export default Contact;
